mapper.insert:
INSERT INTO FILE_T (
    FILE_NO
  , UPLOAD_PATH
  , ORIGINAL_FILENAME
  , FILESYSTEM_NAME
  , HISTORY_NO
) VALUES (
    FILE_SEQ.NEXTVAL
  , #{name}
  , #{mobile} 
  , #{email} 
  , #{address}
  , TO_CHAR(CURRENT_DATE, 'YYYY-MM-DD HH24:MI:SS') 
)

mapper.update:
UPDATE CONTACT_T
  SET NAME = #{name}
  , MOBILE = #{mobile} 
  , EMAIL = #{email} 
  , ADDRESS = #{address} 
  WHERE CONTACT_NO = #{contactNo}

mapper.select:
SELECT CONTACT_NO, NAME, MOBILE, EMAIL, ADDRESS, CREATED_AT
 FROM CONTACT_T
 WHERE CONTACT_NO = #{contactNo}
